import React, { Component } from 'react'

export default class DragDrop extends Component {
  state = {
    tasks: [
      {
        name: 'collect all items from github education pack',
        category: 'wip',
        bgcolor: 'green'
      },
      {
        name: 'get drag and drop working',
        category: 'wip',
        bgcolor: 'orange'
      },
      {
        name: 'finish docs for customers',
        category: 'complete',
        bgcolor: 'red'
      }
    ]
  }

  onDragOver = event => {
    event.preventDefault()
  }

  onDragStart = (event, id) => {
    console.log("dragstart: ", id)
    event.dataTransfer.setData("id", id)
  }

  onDrop = (event, cat) => {
    let id = event.dataTransfer.getData("id");

    let tasks = this.state.tasks.filter(task => {
      if(task.name === id) {
        task.category = cat;
      }
      return task;
    })

    this.setState({ ...this.state, tasks })
  }

  render() {
    let tasks = {
      wip: [],
      complete: []
    }

    this.state.tasks.forEach(task => {
      tasks[task.category].push(
        <div
          key = {task.name}
          onDragStart = { event => this.onDragStart(event, task.name) }
          draggable
          className="draggable"
          style={{
            backgroundColor: task.bgcolor
          }}
        >
          { task.name }
        </div>
      )
    })

    return (
      <div className="container-drag" style = {{

      }}>
        <h2 className="header">
          DRAG AND DROP DEMO
        </h2>
        <div 
          className="wip"
          onDrop = { event => this.onDrop(event, "wip") }
        >
          <span className="task-header">WIP</span>
          { tasks.wip }
        </div>
        <div 
          className="droppable" 
          onDragOver={event => this.onDragOver(event)}
          onDrop = { event => this.onDrop(event, "complete") }
        >
          <span className="task-header">COMPLETED</span>
          { tasks.complete }
        </div>
      </div>
    )
  }
}
