import React, { Component } from 'react';
import TopIcons from '../Desktop/TopIcons/TopIcons';
import ReactButton from './ReactProjects/HoverButton';
import TopContentWindows from '../../components/TopIconsContent/TopContentWindows';

export default class Left extends Component {
  state = {
    data: [
      {
        icon: <i class="fas fa-info-circle"></i>,
        open: false,
        id: 'info'
      },
      {
        icon: <i class="fas fa-project-diagram"></i>,
        open: false,
        id: 'projects'
      },
      {
        icon: <i class="far fa-envelope"></i>,
        open: false,
        id: 'contact'
      }
    ]
  }

  toggleOpen = id => {
    this.setState({ data: this.state.data.map(item => {
      if(item.id == id) {
        item.open = !item.open
        console.log(item.open)
      }
    }) })
  }

  render() { console.log(this.state.data)
    return (
      <div style = { desktopLeft }>
        <div style = { topIconsContainer }>
          { this.state.data.map(item => (
            <TopIcons 
              item = { item }
              toggleOpen = { this.toggleOpen }
            />
          )) }
        </div>
        <div style = { windowsContainer }>
          <TopContentWindows 
            data = { this.state.data }
          />
        </div>
        <div>
          <ReactButton />
        </div>
      </div>
    )
  }
}

const desktopLeft = {
  width: '60%',
  display: 'flex',
  flexDirection: 'column',
  justifyContent: 'space-between',
  alignItems: 'center',
  position: 'relative',
}

const topIconsContainer = {
  display: 'flex',
  justifyContent: 'space-around',
  alignItems: 'center',
  width: '100%',
  fontSize: '1.8rem',
  height: '20%'
}

const windowsContainer = {
  position: 'relative',
  height: '100%',
  width: '100%',
  padding: '5%'
}
