import React, { Component } from 'react'

export default class HoverButton extends Component {
  state = {
    hover: false
  }

  hover = () => {
    this.setState({ hover: !this.state.hover })
  }

  button = hover => {
    return ({
      border: hover ? '0px solid #e7e7e7' : '2px solid #e7e7e7',
      padding: hover ? '15px 25px' : '10px 20px',
      margin: '15% 0',
      transition: hover ? 'all 0.2s ease-out' : 'border 0.2s ease-in'
    });
  }

  render() {
    return (
      <div 
        style = { this.button(this.state.hover) }
        onMouseEnter = { this.hover }
        onMouseLeave = { this.hover }
      >
        React Projects
      </div>
    )
  }
}
