import React, { Component } from 'react';

export default class TopContentWindows extends Component {
  state = {
    top: '-20%',
    height: '0%',
    opacity: '0'
  }



  componentDidMount() {
    setTimeout(() => this.setState({ top: '0', height: '100%', opacity: '1' }), 1)
  }

  render() {
    const { content, open } = this.props.item
    return (
      <div style = { contentStyles }>
        <span style = { this.showContent() }>{ content }</span>
      </div>
    )
  }
}

const contentStyles = {
  position: 'absolute',
  width: '100%',
  border: '2px solid white',
  height: this.state.height,
  top: this.state.top,
  opacity: this.state.opacity,
  transition: 'top 0.5s linear, height 0.3s ease-in 0.2s, opacity 0.5s ease'
}
