import React, { Component } from 'react'

export default class Pomodoro extends Component {
  state = {
    restMin: 5,
    workMin: 20,
    seconds: 0,
    break: false,
    start: false,
    open: false
  }

  timer = () => {
    this.setState({ seconds: this.state.seconds == 0 ? 59 : this.state.seconds - 1 });

    if(this.state.break) {
      this.setState({ restMin: this.state.seconds == 0 ? this.state.restMin - 1 : this.state.restMin == 5 ? 4 : this.state.restMin })

      if(this.state.restMin == -1) {
        this.setState({ restMin: 5, break: false })
      }

    } else {
      this.setState({ workMin: this.state.seconds == 0 ? this.state.workMin - 1 : this.state.workMin == 20 ? 19 : this.state.workMin })

      if(this.state.workMin == -1) {
        this.setState({ workMin: 20, break: true })
      }
    }
  }

  openPomodoro = () => {
    this.setState({ interval: setInterval(this.timer, 1000), start: true })
  }

  btnStyles = open => {
    return ({
      position: 'absolute',
      bottom: '0',
      right: '0',
      border: '2px solid white',
      margin: '2vw',
      width: '150px',
      height: '50px',
      display: 'flex',
      justifyContent: 'center',
      alignItems: 'center',
      zIndex: open ? '-1' : '1'
    });
  }

  pomodoroStyles = open => {
    return ({
      textAlign: 'center',
      fontSize: '3.2rem',
      position: 'absolute',
      display: 'flex',
      flexDirection: 'column',
      right: '-350px',
      bottom: '0',
      margin: '2vw'
    });
  }

  render() {
    return (
      <div style = {{
        fontSize: '2rem',
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        justifyContent: 'space-between',
        fontStyle: 'italic',
      }}>
        <div
          onClick = { () => this.openPomodoro() }
          style = { this.btnStyles(this.state.open) }
        >Pomodoro</div>
        <div style = { this.pomodoroStyles(this.state.open) }>
          <div
            style = {{
              fontSize: '1rem',
              marginBottom: '15px'
            }}
          >a collaborative effort with Claire Sinozich</div>
          <div style = {{ display: 'flex'}}>{ this.state.break ? 'Rest:' : 'Work:' }&nbsp;<div style = {{ fontStyle: 'normal', background: 'white', color: 'black', width: '150px', border: '2px solid white' }}>{ this.state.break ? this.state.restMin : this.state.workMin } : { this.state.seconds < 10 ? `0${this.state.seconds}` : this.state.seconds }</div></div>
        </div>
      </div>
    )
  }
}
