import React, { Component } from 'react';

export default class TopContentWindows extends Component {
  state = {
    open: this.props.open
  }

  contentStyles = open => {
    return({
      position: 'absolute',
      width: '100%',
      height: '100%',
      top: open ? '-50%' : '0',
      transition: 'top 1s linear'
    })
  }

  render() {
    const { content, open } = this.props.item
    return (
      <div style = { this.contentStyles(open) }>
        { content }
      </div>
    )
  }
}
