import React, { Component } from 'react';

export default class TopContentWindows extends Component {
  state = {
    top: '-120%'
  }

  contentStyles = () => {
    return({
      position: 'absolute',
      width: '100%',
      height: '100%',
      top: this.state.top,
      transition: 'top 1s linear'
    })
  }

  componentDidUpdate() {
    this.setState({ top: '0' })
  }

  render() {
    const { content } = this.props.item
    return (
      <div style = { this.contentStyles() }>
        { content }
      </div>
    )
  }
}
