import React, { Component } from 'react'

export default class Pomodoro extends Component {
  state = {
    hover: false
  }

  toggleHover = () => {
    this.setState({ hover: !this.state.hover })
  }

  buttonStyle = (hover) => {
    return ({
      fontSize: hover ? '2rem' : '1.5rem',
      fontStyle: 'italic',
      border: '2px solid white',
      padding: hover ? '10px 20px' : '5px 10px',
      textAlign: 'center',
      transition: 'all 1s ease',
      width: hover ? '250px' : '150px'
    });
  }

  underStyle = hover => {
    return ({
      fontSize: '1rem',
      textAlign: 'center',
      opacity: hover ? '1' : '0',
      transition: 'all 1s ease'
    });
  }

  render() { 
    return (
      <div style = {{
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center'
      }}>
        <div 
          style = { this.buttonStyle(this.state.hover) }
          onMouseEnter = { this.toggleHover }
          onMouseLeave = { this.toggleHover }
        >
          Pomodoro <br />
        </div>
        <span
          style = { this.underStyle(this.state.hover) }
        >
          a collaborative effort with Claire Sinozich
        </span>
      </div>
    )
  }
}
