import React, { Component } from 'react';
import TopIcons from '../Desktop/TopIcons/TopIcons';
import TopContentWindows from '../../components/TopIconsContent/TopContentWindows';
import Info from '../../components/TopIconsContent/Info/Content';
import Projects from '../../components/TopIconsContent/Projects/Content';
import Contact from '../../components/TopIconsContent/Contact/Content';
import ReactButton from './ReactProjects/HoverButton';

export default class Left extends Component {
  state = {
    data: [
      {
        icon: <i class="fas fa-info-circle"></i>,
        content: <Info />,
        open: false,
        id: 'info'
      },
      {
        icon: <i class="fas fa-project-diagram"></i>,
        content: <Projects />,
        open: false,
        id: 'projects'
      },
      {
        icon: <i class="far fa-envelope"></i>,
        content: <Contact />,
        open: false,
        id: 'contact'
      }
    ],
    windowWidth: '100%'
  }

  toggleOpen = id => {
    let openWindow = false;
    this.state.data.map(item => {
      if(item.open) {
        openWindow = true
      }
    })

    if(openWindow) {
      this.setState({ windowWidth: '0%'})
    }

    setTimeout(() => {
      this.setState({ data: this.state.data.map(item => {
        if(item.id == id) {
          item.open = !item.open
        } else {
          item.open = false
        }
        return item;
      }), windowWidth: '100%' })
    }, 500)
  }

  windowsContainer = () => {
    return ({
      position: 'relative',
      height: '65vh',
      width: this.state.windowWidth,
      transition: 'width 0.5s linear'
    })
  }

  render() {
    return (
      <div style = { desktopLeft }>
        <div style = { topIconsContainer }>
          { this.state.data.map(item => (
            <TopIcons 
              item = { item }
              toggleOpen = { this.toggleOpen }
            />
          )) }
        </div>
        <div style = { this.windowsContainer() }>
          { this.state.data.map(item => {
            if(item.open) {
              return (
                <TopContentWindows
                  key = { item.id }
                  item = { item }
                />
              );
            }
          }) }
        </div>
        <div>
          <ReactButton />
        </div>
      </div>
    )
  }
}

const desktopLeft = {
  width: '60%',
  display: 'flex',
  flexDirection: 'column',
  justifyContent: 'space-between',
  alignItems: 'center',
  position: 'relative',
  padding: '0 5%'
}

const topIconsContainer = {
  display: 'flex',
  justifyContent: 'space-around',
  alignItems: 'center',
  width: '100%',
  fontSize: '1.8rem',
  height: '15%'
}
