import React, { Component } from 'react'

export default class DragDrop extends Component {
  state = {
    tasks: [
      {
        name: 'collect all items from github education pack',
        category: 'wip',
        bgcolor: 'green'
      },
      {
        name: 'get drag and drop working',
        category: 'wip',
        bgcolor: 'orange'
      },
      {
        name: 'finish docs for customers',
        category: 'complete',
        bgcolor: 'red'
      }
    ]
  }

  onDragOver = event => {
    event.preventDefault()
  }

  render() {
    let tasks = {
      wip: [],
      complete: []
    }

    this.state.tasks.forEach(task => {
      tasks[task.category].push(
        <div
          key = {task.name}
          draggable
          className="draggable"
          style={{
            backgroundColor: task.bgcolor
          }}
        >
          { task.name }
        </div>
      )
    })

    return (
      <div className="container-drag">
        <h2 className="header">
          DRAG AND DROP DEMO
        </h2>
        <div className="wip">
          <span className="task-header">WIP</span>
          { tasks.wip }
        </div>
        <div className="droppable" onDragOver={event => this.onDragOver(event)}>
          <span className="task-header">COMPLETED</span>
          { tasks.complete }
        </div>
      </div>
    )
  }
}
