import React, { Component } from 'react';
import './index.css';
import Breakpoint, { BreakpointProvider } from 'react-socks';

import Logo from './components/Logo';
import Icons from './components/Icons/IconContainer';
import ReactProjects from './components/ReactProjects/ReactProjects';


export default 
class App extends Component {

  render() {
    return (
      <BreakpointProvider>
        <div 
          style = { body }
        >
          <Breakpoint default up>
            <div style = {{ 
              marginTop: '13%', 
              display: 'flex',
              flexDirection: 'column',
              alignItems: 'center'
            }}>
              <div
                style = {{
                  width: '500px',
                  marginLeft: '8%',
                  justifySelf: 'center'
                }}
              >
                <Logo />
              </div>
              <div
                style = {{
                  display: 'flex',
                  justifyContent: 'space-between',
                  alignItems: 'flex-start',
                  width: '400px',
                  position: 'absolute',
                  top: '5%',
                  right: 'calc(50vw - 220px)'
                }}
              >
                <Icons />
              </div>
                <ReactProjects />
            </div>
            <span style = { under }>personal portfolio; a work in progress</span>
          </Breakpoint>
          <Breakpoint phone only>

          </Breakpoint>
        </div>
      </BreakpointProvider>
    );
  }
}

const body = {
  height: '100vh',
  width: '100vw',
  background: 'radial-gradient(at 0% 0%, #090a0a, #151616 40%, #151617 45%)',
  color: 'white',
  display: 'flex',
  flexDirection: 'column',
  justifyContent: 'flex-end',
  alignItems: 'center',
  overflow: 'none',
}

const under = {
  position: 'absolute',
  bottom: '0',
  fontStyle: 'italic',
  borderRadius: '5px',
  padding: '1% 50px',
  textAlign: 'center',
  color: '#e9e9e9',
  fontSize: '1.2rem'
}
