import React, { Component } from 'react';
import Calc from './Calculator';
import CalcApp from '../../components/ReactProjects/Calculator/App';
import Todo from './Todo';
import Pomodoro from './Pomodoro';

export default class ReactProjects extends Component {
  state = {
    app: false
  }

  reactIcons = open => {
    return ({
      position: 'absolute',
      top: open ? '0' : '125%',
      border: '2px solid white',
      display: 'flex',
      justifyContent: 'space-around',
      alignItems: 'center',
      width: '100%',
      height: app ? '100vw' : '100%',
      opacity: open ? '1' : `0`,
      zIndex: open ? '1' : '-1',
      transition: open ? 'top 1s ease-out' : 'opacity 1s ease-out, z-index 0.1s linear 1s, top 0.1s linear 1s'
    })
  }

  showApp = () => {
    this.setState({ app: !this.state.app })
  }

  showAppStyles = () => {
    return({
      position: 'absolute',
      top: '0',

    })
  }

  render() {
    return (
      <div style = { this.reactIcons(this.props.open) }>
        <Calc 
          onClick = { () => this.showApp() }
        />
        <Todo />
        <Pomodoro />
        <div style = { this.showAppStyles() }>
          <CalcApp />
        </div>
      </div>
    )
  }
}
