import React, { Component } from 'react'

export default class Pomodoro extends Component {
  state = {
    restMin: 5,
    workMin: 19,
    seconds: 0,
    break: false
  }

  timer = () => {
    this.setState({ seconds: this.state.seconds == 0 ? 59 : this.state.seconds - 1 });

    if(this.state.break) {
      this.setState({ restMin: this.state.seconds == 0 ? this.state.restMin - 1 : this.state.restMin })

      if(this.state.restMin == -1) {
        this.setState({ restMin: 5, break: false })
      }

    } else {
      this.setState({ workMin: this.state.seconds == 0 ? this.state.workMin - 1 : this.state.workMin })

      if(this.state.workMin == -1) {
        this.setState({ workMin: 20, break: true })
      }
    }
  }

  componentDidMount() {
    this.setState({ interval: setInterval(this.timer, 1000) })
  }

  render() {
    return (
      <div style = {{
        fontSize: '2rem',
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        justifyContent: 'space-between',
        fontStyle: 'italic',
        height: '150px',
        width: '200px'
      }}>
        <p>
          { this.state.break ? 'Rest' : 'Work' } <span style = {{ fontStyle: 'normal', background: 'white', color: 'black' }}>{ this.state.break ? this.state.restMin : this.state.workMin } : { this.state.seconds < 10 ? `0${this.state.seconds}` : this.state.seconds }</span>
        </p>
      </div>
    )
  }
}
