import React, { Component } from 'react'

export default class DragDrop extends Component {
  state = {
    tasks: [
      {
        name: 'collect all items from github education pack',
        category: 'wip',
        bgcolor: 'green'
      },
      {
        name: 'get drag and drop working',
        category: 'wip',
        bgcolor: 'orange'
      },
      {
        name: 'finish docs for customers',
        category: 'complete',
        bgcolor: 'red'
      }
    ],
    drag: false,
    x: 0,
    y: 0
  }

  onMouseMove = event => {
    this.setState({ x: event.pageX, y: event.pageY })
    console.log(event)
  }

  onDragOver = event => {
    event.preventDefault()
  }

  onDragStart = (event, id) => {
    console.log("dragstart: ", id)
    event.dataTransfer.setData("id", id)
  }

  onDrop = (event, cat) => {
    let id = event.dataTransfer.getData("id");

    let tasks = this.state.tasks.filter(task => {
      if(task.name === id) {
        task.category = cat;
      }
      return task;
    })

    this.setState({ ...this.state, tasks })
  }

  drag = (x, y) => {
    return {
      height: '20px',
      width: '50px',
      background: 'red',
      position: 'absolute',
      top: y - 10,
      left: x - 20
    }
  }

  toggleDrag = () => {
    this.setState({ drag: !this.state.drag })
  }

  render() { console.log(this.state.drag)
    // let tasks = {
    //   wip: [],
    //   complete: []
    // }

    // this.state.tasks.forEach(task => {
    //   tasks[task.category].push(
    //     <div
    //       key = {task.name}
    //       onDragStart = { event => this.onDragStart(event, task.name) }
    //       draggable
    //       className="draggable"
    //       style={{
    //         backgroundColor: task.bgcolor
    //       }}
    //     >
    //       { task.name }
    //     </div>
    //   )
    // })

    return (
      <div
        style = {{
          height: '100vh',
          width: '100vw',
          justifyContent: 'space-between',
          alignItems: 'space-between',
          display: 'flex',
          flexDirection: 'column',
          position: 'relative'
        }}
        onMouseMove = { this.state.drag ? event => this.onMouseMove(event) : null }
      >
        <div style = {this.drag(this.state.x, this.state.y)}
          draggable
          // onDrag = { (event) => this.toggleDrag(event) }
          // onDragEnd = { () => this.toggleDrag() }
        >
          test
        </div>
      </div>
    )
  }
}
