import React, { Component } from 'react';
import Calc from './Calculator';
import CalcApp from '../../components/ReactProjects/Calculator/App';
import Todo from './Todo';
import Pomodoro from './Pomodoro';

export default class ReactProjects extends Component {
  state = {
    app: false
  }

  reactHeader = open => {
    return ({
      width: '100%',
      height: '100%',
      padding: '15px 0',
      textAlign: 'center',
      fontSize: '1.5rem',
      border: '2px solid white',
      zIndex: open ? '-1' : '1',
      opacity: open ? '0' : '1',
      transition: open ? 'opacity 1s ease-out, z-index 0.1s linear 1s' : 'opacity 1s ease-in'
    });
  }

  reactIcons = (open, app) => {
    return ({
      position: 'absolute',
      top: open ? '0' : '125%',
      border: '2px solid white',
      display: 'flex',
      justifyContent: 'space-around',
      alignItems: 'center',
      width: '100%',
      height: app ? '100vw' : '100%',
      opacity: open ? '1' : `0`,
      zIndex: open ? '1' : '-1',
      transition: open ? 'top 1s ease-out' : 'opacity 1s ease-out, z-index 0.1s linear 1s, top 0.1s linear 1s'
    })
  }

  showApp = () => {
    this.setState({ app: !this.state.app })
  }

  showAppStyles = () => {
    return({
      position: 'absolute',
      top: '0',

    })
  }

  render() { console.log(this.state.app)
    return (
      <div 
        style = { reactProjects }
        onMouseEnter = { () => this.showReactProjects() }
        onMouseLeave = { () => this.showReactProjects() }
      >
        <span style = { this.reactHeader(this.state.reactProjects) }>React Projects</span>
        <div style = { this.reactIcons(this.props.open, this.state.app) }>
          <Calc/>
          <Todo />
          <Pomodoro />
        </div>
      </div>
    )
  }
}


const reactProjects = {
  display: 'flex',
  flexWrap: 'wrap',
  justifyContent: 'space-between',
  width: '50%',
  fontSize: '2rem',
  position: 'relative',
  marginBottom: '2.5%',
  cursor: 'pointer'
}
