import React, { Component } from 'react';
import './index.css';
import Breakpoint, { BreakpointProvider } from 'react-socks';

import Logo from './components/Logo';
import Icons from './components/Icons/IconContainer';
import ReactProjects from './components/ReactProjects/ReactProjects';


export default 
class App extends Component {

  render() {
    return (
      <BreakpointProvider>
        <Breakpoint default up>
          <div 
            style = { body }
          >
            <div
              style = {{
                display: 'flex',
                justifyContent: 'space-between',
                alignItems: 'center',
                width: '400px',
                height: '10vh',
                position: 'absolute',
                top: '0'
              }}
            >
              <Icons />
            </div>
            <div
              style = {{
                width: '500px',
                height: '400px',
                display: 'flex',
                flexDirection: 'column',
                justifyContent: 'space-evenly',
                alignItems: 'center',
                paddingBottom: '5%'
              }}
            >
              <Logo />
              <ReactProjects />
            </div>
            <span style = { under }>personal portfolio; a work in progress</span>
          </div>
        </Breakpoint>
        <Breakpoint phone only>

        </Breakpoint>
      </BreakpointProvider>
    );
  }
}

const body = {
  height: '100vh',
  width: '100vw',
  background: 'radial-gradient(at 0% 0%, #090a0a, #151616 40%, #151617 45%)',
  color: 'white',
  display: 'flex',
  flexDirection: 'column',
  justifyContent: 'center',
  alignItems: 'center',
  overflow: 'none',
}

const under = {
  position: 'absolute',
  bottom: '0',
  fontStyle: 'italic',
  borderRadius: '5px',
  padding: '1% 50px',
  textAlign: 'center',
  color: '#e9e9e9',
  fontSize: '1.2rem'
}
