import React, { Component } from 'react';
import './index.css';

import Icons from './Layout/Icons/IconContainer';
import CalcApp from './Layout/ReactProjects/Calculator';
import TodoApp from './Layout/ReactProjects/Todo';
import Signature from './Layout/Signature';
import Logo from './Layout/Logo';
import Pomodoro from './Layout/ReactProjects/Pomodoro';

{/* <i class="fas fa-user-clock"></i> */}

export default 
class App extends Component {
  state = {
    reactProjects: false
  }

  reactProjects = open => {
    return ({
      display: 'flex',
      flexWrap: 'wrap',
      justifyContent: 'space-between',
      width: '50%',
      border: '2px solid orange',
      fontSize: '2.5rem',
      marginTop: open ? '80px' : '0'
    })
  }

  render() {
    return (
      <div style = { desktopContainer }>
        <div style = { pageLeftContent }>
          <div style = { desktopIconStyles }>
            <Icons />
          </div>
          <div style = { this.reactProjects(this.state.reactProjects) }>
            <span 
              style = { reactHeader }

            >React Projects</span>
            <CalcApp />
            <TodoApp />
            <Pomodoro />
          </div>
        </div>
        <div style = { logoContainer }>
          <div style = { logoImg }>
            <Logo />
          </div>
          <div style = { logoText }>
            <Signature />
          </div>
        </div>
      </div>
    );
  }
}

const desktopContainer = {
  height: '100vh',
  width: '100vw',
  background: 'linear-gradient(145deg, #090a0a 45%, #151617 75%)',
  color: 'white',
  display: 'flex',
  justifyContent: 'space-between',
  border: '2px solid green'
}

const pageLeftContent = {
  display: 'flex',
  flexDirection: 'column',
  width: '55%',
  padding: '0 1%',
  justifyContent: 'space-between',
  alignItems: 'center',
  border: '2px solid blue'
}

const desktopIconStyles = {
  display: 'flex',
  justifyContent: 'space-between',
  alignItems: 'center',
  width: '100%',
  padding: '5%',
  position: 'relative',
  border: '2px solid purple'
}

const reactHeader = {
  width: '100%',
  padding: '20px 0',
  textAlign: 'center',
  fontSize: '2rem',
  border: '2px solid white'
}

const logoContainer = {
  display: 'flex',
  flexDirection: 'column',
  justifyContent: 'center',
  alignItems: 'center',
  border: '2px solid red',
  width: '45%'
}

const logoImg = {
  height: '75vh',
}

const logoText = {
  width: '50%'
}
