import React, { Component } from 'react';
import './index.css';

import Icons from './Layout/Icons/IconContainer';
import ReactProjects from './Layout/ReactProjects/ReactProjects';
import Signature from './Layout/Signature';
import Logo from './Layout/Logo';


{/* <i class="fas fa-user-clock"></i> */}

export default 
class App extends Component {
  state = {
    reactProjects: false
  }

  reactHeader = open => {
    return ({
      width: '100%',
      height: '100%',
      padding: '15px 0',
      textAlign: 'center',
      fontSize: '1.5rem',
      border: '2px solid white',
      zIndex: open ? '-1' : '1',
      opacity: open ? '0' : '1',
      transition: open ? 'opacity 1s ease-out, z-index 0.1s linear 1s' : 'opacity 1s ease-in'
    });
  }

  showReactProjects = () => {
    this.setState({ reactProjects: !this.state.reactProjects })
  }

  render() {
    return (
      <div style = { desktopContainer }>
        <div style = { pageLeftContent }>
          <div style = { desktopIconStyles }>
            <Icons />
          </div>
          <div 
            style = { reactProjects }
            onMouseEnter = { () => this.showReactProjects() }
            onMouseLeave = { () => this.showReactProjects() }
          >
            <span style = { this.reactHeader(this.state.reactProjects) }>React Projects</span>
            <ReactProjects 
              open = { this.state.reactProjects }
            />
          </div>
        </div>
        <div style = { logoContainer }>
          <div style = { logoImg }>
            <Logo />
          </div>
          <div style = { logoText }>
            <Signature />
          </div>
        </div>
      </div>
    );
  }
}

const desktopContainer = {
  height: '100vh',
  width: '100vw',
  background: 'linear-gradient(145deg, #090a0a 45%, #151617 75%)',
  color: 'white',
  display: 'flex',
  justifyContent: 'space-between'
}

const pageLeftContent = {
  display: 'flex',
  flexDirection: 'column',
  width: '55%',
  padding: '0 2%',
  justifyContent: 'space-between',
  alignItems: 'center'
}

const desktopIconStyles = {
  display: 'flex',
  justifyContent: 'space-around',
  alignItems: 'center',
  width: '100%',
  padding: '2.5% 5%',
  position: 'relative',
  marginTop: '2.5%'
}

const reactProjects = {
  display: 'flex',
  flexWrap: 'wrap',
  justifyContent: 'space-between',
  width: '50%',
  fontSize: '2rem',
  position: 'relative',
  marginBottom: '2.5%',
  cursor: 'pointer'
}

const logoContainer = {
  display: 'flex',
  flexDirection: 'column',
  justifyContent: 'center',
  alignItems: 'center',
  width: '45%'
}

const logoImg = {
  height: '75vh',
}

const logoText = {
  width: '50%'
}
