import React, { Component } from 'react';
import Calc from './Calculator';
import CalcApp from '../../components/ReactProjects/Calculator/App';
import Todo from './Todo';
import TodoApp from '../../components/ReactProjects/Todo/App';
import Pomodoro from './Pomodoro';

export default class ReactProjects extends Component {
  state = {
    icons: false,
    app: false,
    calc: false,
    todo: false 
  }

  showIcons = () => {
    this.setState({ icons: !this.state.icons })
  }

  reactContainer = open => {
    return ({
      display: 'flex',
      JustifyContent: 'center',
      alignItems: 'center',
      border: '2px solid red',
      width: open ? '100%' : '50%',
      height: open ? '100%' : '60px',
      marginBottom: '5%'
    })
  }

  reactProjects = open => {
    return({
      display: 'flex',
      flexWrap: 'wrap',
      justifyContent: 'space-between',
      width: open ? '50%' : '100%',
      fontSize: '2rem',
      position: 'relative',
      marginBottom: '2.5%',
      cursor: 'pointer',

    });
  }

  reactHeader = (open, app) => {
    return ({
      width: '100%',
      height: '100%',
      padding: '15px 0',
      textAlign: 'center',
      fontSize: '1.5rem',
      border: '2px solid white',
      zIndex: open ? '-1' : '1',
      opacity: open ? '0' : '1',
      transition: open ? 'opacity 1s ease-out, z-index 0.1s linear 1s' : 'opacity 1s ease-in',
    });
  }

  reactIcons = (open, app) => {
    return ({
      position: 'absolute',
      top: open ? '0' : '125%',
      border: app ? 'none' : '2px solid white',
      display: 'flex',
      justifyContent: 'space-around',
      alignItems: 'center',
      width: '100%',
      height: '100%',
      opacity: open ? '1' : `0`,
      zIndex: open ? '1' : '-1',
      transition: open ? 'top 1s ease-out' : 'opacity 1s ease-out, z-index 0.1s linear 1s, top 0.1s linear 1s'
    })
  }

  showApp = () => {
    this.setState(prevState => {
      return {
        icons: prevState.icons,
        app: true,
        calc: !prevState.calc,
        todo: !prevState.todo 
      }
    })
  }

  showCalc = () => {
    this.setState({ calc: !this.state.calc, app: true })
  }

  showTodo = () => {
    this.setState({ todo: !this.state.todo, app: true })
  }

  showAppStyles = open => {
    return({
      position: 'absolute',
      top: open ? '15vh' : '100vh',
      border: '2px solid gray',
      height: '65%',
      width: '30%',
      padding: '2% 5%',
      background: '#e9e9e9',
      zIndex: open ? '1' : '-1'
    })
  }

  render() { console.log(this.state.app, this.state.calc, this.state.todo)
    return (
      <div style = { this.reactContainer(this.state.app) }>
        <div 
          style = { this.reactProjects(this.state.app) }
          onMouseEnter = { this.state.app ? null : () => this.showIcons() }
          onMouseLeave = { this.state.app ? null : () => this.showIcons() }
        >
          <span style = { this.reactHeader(this.state.icons, this.state.app) }>React Projects</span>
          <div style = { this.reactIcons(this.state.icons, this.state.app) }>
            <Calc 
              open = { this.state.app ? this.showApp : this.showCalc }
            />
            <Todo 
              open = { this.state.app ? this.showApp : this.showTodo }
            />
            <Pomodoro />
          </div>
        </div>
        <div 
          style = { this.showAppStyles(this.state.calc) }
        >
          <CalcApp />
        </div>
        <div 
          style = { this.showAppStyles(this.state.todo) }
        >
          <TodoApp />
        </div>
      </div>
    )
  }
}
